mapper.select:
SELECT PROMOTIONNO, TRAVELNO, CATEGORYNAME
 FROM CATEGORY
 where promotionNO=#{promotionNO}

mapper.update:
UPDATE CATEGORY SET
  CATEGORYNAME=#{categoryName}
  where promotionNO=#{promotionNO}

mapper.select:
SELECT PROMOTIONNO, TRAVELNO, CATEGORYNAME
 FROM CATEGORY
 where travelNO=#{travelNO}

mapper.update:
UPDATE CATEGORY SET
  CATEGORYNAME=#{categoryName}
  where travelNO=#{travelNO}
