mapper.select:
SELECT PROMOTIONNO, AREA, FOOD, TRAFFIC, CULTURE, HEALING, LEISURE
 FROM CATEGORY
 where promotionNO=#{promotionNO}

mapper.update:
UPDATE CATEGORY SET
  AREA=#{area}, FOOD=#{food}, TRAFFIC=#{traffic}, CULTURE=#{culture}, HEALING=#{healing}, LEISURE=#{leisure}
  where promotionNO=#{promotionNO}
