mapper.select:
 
		SELECT image1,image2,image3,image4,image5,
		 image6,image7,image8,image9,image10 from image where noticeNO=#{noticeNO} or travelNO=#{travelNO}
		 or reviewNO=#{reviewNO} or promotionNO=#{promotionNO} or eventNO=#{eventNO}

mapper.update:
 
		UPDATE IMAGE SET image1=#{image1}, image2=#{image2},image3=#{image3},
		image4=#{image4},image5=#{image5},image6=#{image6},image7=#{image7} 
		WHERE noticeNO=#{noticeNO} or travelNO=#{travelNO}
		 or reviewNO=#{reviewNO} or promotionNO=#{promotionNO} or eventNO=#{eventNO}
